import React, { useState } from "react";

import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";

function NewExpense(props) {
  // receive and save new expense data from ExpenseForm component
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    //console.log(expenseData);

    props.onAddExpense(expenseData);
  };

  const [expenseFormContent, setExpenseFormContent] = useState(
    newExpenseDefaultContent
  );

  // let expenseFormContent = "";

  const revealExpenseFormHandler = () => {
    console.log("expense form here");

    setExpenseFormContent(
      <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
    );
  };

  const newExpenseDefaultContent = (
    <button
      type="submit"
      className="show-form-btn"
      onClick={revealExpenseFormHandler}
    >
      Add New Expense
    </button>
  );

  return <div className="new-expense">{expenseFormContent}</div>;
}

export default NewExpense;
