import React from "react";
import Card from "../UI/Card";

import "./Chart.css";
import ChartBar from "./ChartBar";

function Chart(props) {
  const chartDataPoints = [
    { label: "Jan", value: 0 },
    { label: "Feb", value: 0 },
    { label: "Mar", value: 0 },
    { label: "Apr", value: 0 },
    { label: "May", value: 0 },
    { label: "Jun", value: 0 },
    { label: "Jul", value: 0 },
    { label: "Aug", value: 0 },
    { label: "Sep", value: 0 },
    { label: "Oct", value: 0 },
    { label: "Nov", value: 0 },
    { label: "Dec", value: 0 },
  ];



  for (const expense of props.expenses) {
    const expenseMonth = expense.date.getMonth(); // 0 = Jan, 1 = Feb, etc.
    chartDataPoints[expenseMonth].value += expense.amount;
  }const dataValues = chartDataPoints.map((dataPoint) => dataPoint.value);
  const maxDataPoint = Math.max(...dataValues);

  return (
    <Card className="chart">
      {chartDataPoints.map((dataPoint) => (
        <ChartBar
          key={dataPoint.label}
          label={dataPoint.label}
          value={dataPoint.value}
          maxValue={maxDataPoint}
        ></ChartBar>
      ))}
    </Card>
  );
}

export default Chart;
