import React, { useState } from "react";

import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("Select Year");
  const [filteredExpenses, setFilteredExpenses] = useState(props.items);
  let expenses = [];

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
    //console.log(selectedYear);
    setFilteredExpenses(
      props.items.filter(
        (expense) => expense.date.getFullYear() === Number(selectedYear)
      )
    );
    //console.log(filteredExpenses);
    if(filteredYear === 'Select Year') expenses = props.items;
    else expenses = filteredExpenses;
  };
  console.log(filteredYear, filteredExpenses);

  return (
    <Card className="expenses">
      <ExpensesFilter
        selected={filteredYear}
        onChangeFilter={filterChangeHandler}
      />
      {props.items.map((expense) => {
        return (
          <ExpenseItem
            key={expense.id}
            date={expense.date}
            title={expense.title}
            amount={expense.amount}
          />
        );
      })}
    </Card>
  );
}

export default Expenses;
